import { useState, useEffect } from "react";
import SearchBar from "./SearchBar";
import ProductsHeading from "./ProductsHeading";

export default function ProductsPicker() {
    let [products, setProducts] = useState('')
    let [searchValue, setSearchValue] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [data, setData] = useState([]);

    const getProducts = async (searchValue) => {
        
      const url = `https://fakestoreapi.com/products/${searchValue}`;
      const response = await fetch(url);
      const data = await response.json();
      console.log(data);

      if (data) {
        setData(data);
      }
    };

  useEffect(() => {
    getProducts(searchValue);
  }, [searchValue]);

  useEffect(() => {
    console.log(data);
  }, [data]);

  return (
    <div>
      <ProductsHeading heading='Products' />

        <SearchBar
        searchValue={searchValue} 
        setSearchValue={setSearchValue}
        >
          Search
        </SearchBar>
          <div>
          {data.map((product) => (
            <div key={product.id} className='card'>
            <div><img src={product.image} alt='product'/></div>
            <div className="card-description">
              <h1>{product.title}</h1>
              </div>
            </div>
          ))}          
          </div>
          {data.map((product) => (
            <div key={product.id} className='card'>
            <div><img src={product.image} alt='product'/></div>
            <div className="card-description">
              <h1>{product.title}</h1>
              </div>
            </div>
          ))}
    </div>
    );
  }
