import { useState, useEffect } from "react";
import SearchBar from "./SearchBar";

export default function ProductsPicker() {
    let [products, setProducts] = useState('')
    let [searchValue, setSearchValue] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [data, setData] = useState([]);

    const getProducts = async (searchValue) => {
        
      const url = `https://fakestoreapi.com/products/${searchValue}`;
      const response = await fetch(url);
      const data = await response.json();
      console.log(data);

      if (data) {
        setData(data);
      }
    };

  useEffect(() => {
    getProducts(searchValue);
  }, [searchValue]);

  useEffect(() => {
    console.log(data);
  }, [data]);

  return (
    <div>
        <SearchBar
        searchValue={searchValue} 
        setSearchValue={setSearchValue}
        >
          Search
        </SearchBar>
          <div className="product-section">
            {data.slice(0,1,2,3,4,5).map(product)((product) => (
              <div key={product.id} className='card'>
                {/* <div><img src={product.image} alt='product'/></div>
                <div className="card-description">
                  <h1>${product.price}</h1>
                </div> */}
                <h1>CSS-only Carousel</h1>

                <p>This carousel is created with HTML and CSS only.</p>

                <section class="carousel" aria-label="Gallery">
                  <ol class="carousel__viewport">
                    <li id="carousel__slide1"
                        tabindex="0"
                        class="carousel__slide">
                      <div class="carousel__snapper">
                        <a href="#carousel__slide4"
                          class="carousel__prev">Go to last slide</a>
                        <a href="#carousel__slide2"
                          class="carousel__next">Go to next slide</a>
                      </div>
                    </li>
                    <li id="carousel__slide2"
                        tabindex="0"
                        class="carousel__slide">
                      <div class="carousel__snapper"></div>
                      <a href="#carousel__slide1"
                        class="carousel__prev">Go to previous slide</a>
                      <a href="#carousel__slide3"
                        class="carousel__next">Go to next slide</a>
                    </li>
                    <li id="carousel__slide3"
                        tabindex="0"
                        class="carousel__slide">
                      <div class="carousel__snapper"></div>
                      <a href="#carousel__slide2"
                        class="carousel__prev">Go to previous slide</a>
                      <a href="#carousel__slide4"
                        class="carousel__next">Go to next slide</a>
                    </li>
                    <li id="carousel__slide4"
                        tabindex="0"
                        class="carousel__slide">
                      <div class="carousel__snapper"></div>
                      <a href="#carousel__slide3"
                        class="carousel__prev">Go to previous slide</a>
                      <a href="#carousel__slide1"
                        class="carousel__next">Go to first slide</a>
                    </li>
                  </ol>
                  <aside class="carousel__navigation">
                    <ol class="carousel__navigation-list">
                      <li class="carousel__navigation-item">
                        <a href="#carousel__slide1"
                          class="carousel__navigation-button">Go to slide 1</a>
                      </li>
                      <li class="carousel__navigation-item">
                        <a href="#carousel__slide2"
                          class="carousel__navigation-button">Go to slide 2</a>
                      </li>
                      <li class="carousel__navigation-item">
                        <a href="#carousel__slide3"
                          class="carousel__navigation-button">Go to slide 3</a>
                      </li>
                      <li class="carousel__navigation-item">
                        <a href="#carousel__slide4"
                          class="carousel__navigation-button">Go to slide 4</a>
                      </li>
                    </ol>
                  </aside>
                </section>
              </div>
            ))}     
              
          </div>
    </div>
    );
  }
