import { useState, useEffect } from "react";
import ProductList from "./ProductList";
import SearchBar from "./SearchBar";
import ProductsHeading from "./ProductsHeading";

export default function ProductsPicker() {
    let [products, setProducts] = useState('')
    let [searchValue, setSearchValue] = useState('');
    const [isModalOpen, setIsModalOpen] = useState(false);

    const getProducts = async (searchValue) => {
        
      const url = `https://fakestoreapi.com/products/${searchValue}`;
      const response = await fetch(url);
      const data = await response.json();
      console.log(data);

      if (data) {
        setProducts(data);
      }
    };

  useEffect(() => {
    getProducts(searchValue);
  }, [searchValue]);

  return (
    <div>
      <>
			{products.map((product, index) => (
				<div className='image-container'>
					<img src={product.image} alt='products'></img>
				</div>
			))}
		</>
      <ProductsHeading heading='Products' />

        <SearchBar
        searchValue={searchValue} 
        setSearchValue={setSearchValue}
        >
          Search
        </SearchBar>
        <ProductList products={products}>

        </ProductList>
    </div>
    );
  }
